DROP TABLE IF EXISTS `applican_biodata`;
CREATE TABLE `applican_biodata` (
  `id_biodata` int(11) NOT NULL AUTO_INCREMENT,
  `id_user` int(11) NOT NULL,
  `nama_depan` varchar(225) NOT NULL,
  `nama_belakang` varchar(225) NOT NULL,
  `jabatan` varchar(225) NOT NULL,
  `no_hp` varchar(225) NOT NULL,
  `email` varchar(225) NOT NULL,
  `lembaga_nama` varchar(225) NOT NULL,
  `lembaga_tahun_berdiri` varchar(225) NOT NULL,
  `lembaga_bidang_kegiatan` varchar(225) NOT NULL,
  `lembaga_alamat` varchar(225) NOT NULL,
  `lembaga_profil` varchar(225) NOT NULL,
  `lembaga_provinsi` varchar(225) NOT NULL,
  `lembaga_kota` varchar(225) NOT NULL,
  `lembaga_kecamatan` varchar(225) NOT NULL,
  `lembaga_kodepos` varchar(225) NOT NULL,
  `lembaga_telpon` varchar(225) NOT NULL,
  `lembaga_faxmili` varchar(225) NOT NULL,
  `lembaga_email` varchar(225) NOT NULL,
  `lembaga_web` varchar(225) NOT NULL,
  `status` enum('0','1') NOT NULL DEFAULT '0',
  PRIMARY KEY (`id_biodata`),
  UNIQUE KEY `id_auth` (`id_user`),
  CONSTRAINT `applican_biodata_ibfk_1` FOREIGN KEY (`id_user`) REFERENCES `sf_guard_user` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

INSERT INTO `applican_biodata` (`id_biodata`, `id_user`, `nama_depan`, `nama_belakang`, `jabatan`, `no_hp`, `email`, `lembaga_nama`, `lembaga_tahun_berdiri`, `lembaga_bidang_kegiatan`, `lembaga_alamat`, `lembaga_profil`, `lembaga_provinsi`, `lembaga_kota`, `lembaga_kecamatan`, `lembaga_kodepos`, `lembaga_telpon`, `lembaga_faxmili`, `lembaga_email`, `lembaga_web`, `status`) VALUES
