DROP TABLE IF EXISTS `tagging_berita`;
CREATE TABLE `tagging_berita` (
  `id` varchar(255) NOT NULL,
  `tag_id` varchar(255) NOT NULL,
  `berita_id` varchar(255) NOT NULL,
  `taggable_model` varchar(30) NOT NULL,
  `taggable_id` int(4) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_tagging` (`tag_id`),
  KEY `FK_tagging2` (`berita_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 ROW_FORMAT=DYNAMIC;


DROP TABLE IF EXISTS `tag_berita`;
