  `triple_value` varchar(100) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `widget`;
CREATE TABLE `widget` (
  `id_widget` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(225) NOT NULL,
  `content_ID` text,
  `content_EN` text,
  `urutan` int(11) NOT NULL,
  `tipe` enum('text','img') DEFAULT 'text',
  PRIMARY KEY (`id_widget`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

INSERT INTO `widget` (`id_widget`, `name`, `content_ID`, `content_EN`, `urutan`, `tipe`) VALUES
(3,	'about-us',	'n-a-blog.png',	'n-a-blog.png',	2,	'img'),
(4,	'about-us',	'Yayasan Bersama Lestarikan Nusantara (“Yayasan Belantara”) is an Indonesian grant-making institution formed in 2014 with the goal of delivering wide- ranging community and conservation results. It takes its name from the Indonesian word ‘Belantara’ which means wilderness or pristine forest.\r\n\r\nBelantara primary focus is to allocate grants to support restoration, protection, conservation of endangered species (speci cally Sumatran Tiger, Sumatran Elephant, as well as Sumatran and Bornean Orangutan), Institutional development, and community development and empowerment initiatives in Conservation Area, Production Forest, Protection Forest, and Social Forestry on the ten speci ed grant distribution areas across ve provinces on the islands of Sumatra and Kalimantan (Indonesian Borneo)',	'Yayasan Bersama Lestarikan Nusantara (“Yayasan Belantara”) is an Indonesian grant-making institution formed in 2014 with the goal of delivering wide- ranging community and conservation results. It takes its name from the Indonesian word ‘Belantara’ which means wilderness or pristine forest.\r\n\r\nBelantara primary focus is to allocate grants to support restoration, protection, conservation of endangered species (speci cally Sumatran Tiger, Sumatran Elephant, as well as Sumatran and Bornean Orangutan), Institutional development, and community development and empowerment initiatives in Conservation Area, Production Forest, Protection Forest, and Social Forestry on the ten speci ed grant distribution areas across ve provinces on the islands of Sumatra and Kalimantan (Indonesian Borneo)',	1,	'text'),
(5,	'about-us',	'PA5.jpg',	'PA5.jpg',	3,	'img'),
(6,	'about-us',	'Working with local communities, governments, the private sector and NGOs, Belantara relies on a multi-stakeholder approach to better inform decision-making when addressing resource management problems s the ssential Ecosystem Areas (KEE) stretch across the grant distribution areas, a coordinated response bet een all stakeholders for the effective management and preservation of critical ecosystems is required.',	'Working with local communities, governments, the private sector and NGOs, Belantara relies on a multi-stakeholder approach to better inform decision-making when addressing resource management problems s the ssential Ecosystem Areas (KEE) stretch across the grant distribution areas, a coordinated response bet een all stakeholders for the effective management and preservation of critical ecosystems is required.',	4,	'text'),
(7,	'about-us',	'As an independent foundation, Belantara aims to work with all parties that shares its goals, coordinating and collaborating ith partner pro ects ithin each of its ten speci ed grant distribution areas elantara aims to ensure that existing initiatives are aligned, minimizing the risk of projects overlapping while maximizing information and data sharing. \r\nCo-founder sia ulp and aper signi cantly contributed in getting the oundation off the ground, providing substantial nancing dditional nancial resources are being raised from the public and private sectors, hile investment de-risking initiatives will follow to achieve a more holistic scope of nancing modalities. \r\nFundamentally, Belantara fully supports the focus and priority initiatives of the Ministry of Environment and Forestry of government of ndonesia To be speci c, elantara supports the Development of Large-Scale Ecosystem Conservation in Sumatra and Kalimantan.',	'As an independent foundation, Belantara aims to work with all parties that shares its goals, coordinating and collaborating ith partner pro ects ithin each of its ten speci ed grant distribution areas elantara aims to ensure that existing initiatives are aligned, minimizing the risk of projects overlapping while maximizing information and data sharing. \r\nCo-founder sia ulp and aper signi cantly contributed in getting the oundation off the ground, providing substantial nancing dditional nancial resources are being raised from the public and private sectors, hile investment de-risking initiatives will follow to achieve a more holistic scope of nancing modalities. \r\nFundamentally, Belantara fully supports the focus and priority initiatives of the Ministry of Environment and Forestry of government of ndonesia To be speci c, elantara supports the Development of Large-Scale Ecosystem Conservation in Sumatra and Kalimantan.',	5,	'text'),
(8,	'about-us',	'PA6.jpg',	'PA6.jpg',	6,	'img'),
(9,	'about-us',	'To do so, in 2014 Belantara initiated a creation of Belantara’s Guideline document, which involved a consortium of private sector groups, government and NGO stakeholders during its development process. The main ob ective of the elantara s uideline document is to align conservation activities carried out by multiple stakeholders. The Belantara’s Guideline, which is a multi-stakeholder action plan serves as a supporting guideline for partner groups implementing pro ects ithin the speci ed grant distribution areas,',	'To do so, in 2014 Belantara initiated a creation of Belantara’s Guideline document, which involved a consortium of private sector groups, government and NGO stakeholders during its development process. The main ob ective of the elantara s uideline document is to align conservation activities carried out by multiple stakeholders. The Belantara’s Guideline, which is a multi-stakeholder action plan serves as a supporting guideline for partner groups implementing pro ects ithin the speci ed grant distribution areas,',	7,	'text'),
(10,	'about-us',	'creates an enabling environment for building consensus and capacity among stakeholders, and seeks to ensure that the initiatives in the ecosystem collectively support an environmentally, socially, culturally, and economically sustainable ecosystem. The Belantara’s Guideline document promotes the Multi- stakeholders pproach for a Sustainable cosystem anagement, identi es ten speci ed grant distribution areas based on a de ned set of criteria, and outlines a number of activities and programs in support of ecosystem strategies relevant to the ecosystems.',	'creates an enabling environment for building consensus and capacity among stakeholders, and seeks to ensure that the initiatives in the ecosystem collectively support an environmentally, socially, culturally, and economically sustainable ecosystem. The Belantara’s Guideline document promotes the Multi- stakeholders pproach for a Sustainable cosystem anagement, identi es ten speci ed grant distribution areas based on a de ned set of criteria, and outlines a number of activities and programs in support of ecosystem strategies relevant to the ecosystems.',	8,	'text'),
(12,	'grant-distribution-area',	'gd-map.png',	'gd-map.png',	1,	'img'),
(13,	'grant-distribution-area',	'For the next  Five years,  Belantara has chosen to focus the vast majority of its conservation supports on ten grant distribution areas  hose locations span five provinces and to islands (Sumatra and kalimantan These ten specified grant distribution areas cover 10,145,187.85 hectares of land that includes 4 national parks, 9 wildlife areas, 4 nature reserves, and 2 grand forest parks; as well as Biosphere Reserves. Just over 1,000 vertebrate species have been identified within Belantara\'s grant distribution areas, including 209 species that are protected by the overnment of Indonesia and 213 species that are globally endangered according to IUCN\'s Red List. ',	'For the next  Five years,  Belantara has chosen to focus the vast majority of its conservation supports on ten grant distribution areas  hose locations span five provinces and to islands (Sumatra and kalimantan These ten specified grant distribution areas cover 10,145,187.85 hectares of land that includes 4 national parks, 9 wildlife areas, 4 nature reserves, and 2 grand forest parks; as well as Biosphere Reserves. Just over 1,000 vertebrate species have been identified within Belantara\'s grant distribution areas, including 209 species that are protected by the overnment of Indonesia and 213 species that are globally endangered according to IUCN\'s Red List. ',	2,	'text'),
(14,	'grant-distribution-area',	'Indicators for the identification of the priority grant distribution areas weere based on:\r\n        		<ul class=\"list-icon list-icon-check list-icon-colored m-b-10\">\r\n        			<li class=\"lihome\">Biogeography, hydrology, ecosystem biodiversity and carbon characteristic.</li>\r\n        			<li class=\"lihome\">Uniqueness of the area including its social and cultural features.</li>\r\n        			<li class=\"lihome\">Its function and spatial governance.</li>\r\n        			<li class=\"lihome\">According to the legal regulations, such as natural reserve area and natural conservation area, protection forest, peatland (>3m depth, riparian, coastal areas, high biodiversity area, and so forth</li>\r\n        		</ul>',	'Indicators for the identification of the priority grant distribution areas weere based on:\r\n        		<ul class=\"list-icon list-icon-check list-icon-colored m-b-10\">\r\n        			<li class=\"lihome\">Biogeography, hydrology, ecosystem biodiversity and carbon characteristic.</li>\r\n        			<li class=\"lihome\">Uniqueness of the area including its social and cultural features.</li>\r\n        			<li class=\"lihome\">Its function and spatial governance.</li>\r\n        			<li class=\"lihome\">According to the legal regulations, such as natural reserve area and natural conservation area, protection forest, peatland (>3m depth, riparian, coastal areas, high biodiversity area, and so forth</li>\r\n        		</ul>',	3,	'text'),
(15,	'our-approach',	'approach2.png',	'approach2.png',	1,	'img'),
(16,	'our-approach',	'approach3.png',	'approach3.png',	2,	'img'),
(17,	'our-approach',	'The  elantara foundation believes that conservation must incorporate a harmony of principles that not only protect the interests of the people, particularly at the grass-roots level but also protect natural resources and bio-diversity long into the future- on a large scale   elantara believes all of the actors in a given ecosystem should communicate, collaborate and coordinate to manage a sustainable ecosystem, since a result of balance condition bet een long-term economic development, sustainable livelihoods for local communities, and environmental conservation  ill be bene cial for all of the stakeholders In Indonesia like elsewhere, forests are part of a mosaic that interconnects with other land uses such as agriculture and infrastructure - sometimes in a context of poverty, inequity and poorly planned economic development.',	'The  elantara foundation believes that conservation must incorporate a harmony of principles that not only protect the interests of the people, particularly at the grass-roots level but also protect natural resources and bio-diversity long into the future- on a large scale   elantara believes all of the actors in a given ecosystem should communicate, collaborate and coordinate to manage a sustainable ecosystem, since a result of balance condition bet een long-term economic development, sustainable livelihoods for local communities, and environmental conservation  ill be bene cial for all of the stakeholders In Indonesia like elsewhere, forests are part of a mosaic that interconnects with other land uses such as agriculture and infrastructure - sometimes in a context of poverty, inequity and poorly planned economic development.',	3,	'text'),
(18,	'our-approach',	'approach1.png',	'approach1.png',	4,	'img'),
(19,	'our-approach',	'This realization calls for prioritizing larger-scale, multi-stakeholder approach instead of sectoral silos and putting people on the same footing as environmental values, therefore, encouraging new and innovative ways for the conservation of ecosystems. Central to Belantara’s approach is facilitating multi- stakeholder forum from the ground up. Through the forum, the stakeholders centered within the ecosystem are enabled to design a conservation plan that is long-term and sustainable but also supports their economic needs. It put local communities, government authorities,    s, and businesses as the key actors for the change.',	'This realization calls for prioritizing larger-scale, multi-stakeholder approach instead of sectoral silos and putting people on the same footing as environmental values, therefore, encouraging new and innovative ways for the conservation of ecosystems. Central to Belantara’s approach is facilitating multi- stakeholder forum from the ground up. Through the forum, the stakeholders centered within the ecosystem are enabled to design a conservation plan that is long-term and sustainable but also supports their economic needs. It put local communities, government authorities,    s, and businesses as the key actors for the change.',	5,	'text'),
(20,	'our-program',	'peoples.png',	'peoples.png',	1,	'img'),
(21,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">CONSENSUS FOR CONSERVATION PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To promote an inclusive process that galvanizes commitments from stakeholders involved in each individual ecosystem, develop and promote program, and monitor activities during the project implementation phase. To develop and maintain stakeholder engagement and commitment in each ecosystem for a sustainable ecosystem management in the long term.</p>',	'<strong style=\"text-align: center; font-weight: 800;\">CONSENSUS FOR CONSERVATION PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To promote an inclusive process that galvanizes commitments from stakeholders involved in each individual ecosystem, develop and promote program, and monitor activities during the project implementation phase. To develop and maintain stakeholder engagement and commitment in each ecosystem for a sustainable ecosystem management in the long term.</p>',	2,	'text'),
(22,	'our-program',	'board.png',	'board.png',	3,	'img'),
(23,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">INSTITUTIONAL DEVELOPMENT PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To strengthen existing and create necessary new institutions in order for ecosystem conservation to evolve from shared ideas into collective action. To facilitate multi-stakeholder institutions to adopt ecosystem conservation work plans agreed and legali ed upon by all relevant institutions in each ecosystem and integrated into relevant policies.</p>',	'<strong style=\"text-align: center; font-weight: 800;\">INSTITUTIONAL DEVELOPMENT PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To strengthen existing and create necessary new institutions in order for ecosystem conservation to evolve from shared ideas into collective action. To facilitate multi-stakeholder institutions to adopt ecosystem conservation work plans agreed and legali ed upon by all relevant institutions in each ecosystem and integrated into relevant policies.</p>',	4,	'text'),
(24,	'our-program',	'seed.png',	'seed.png',	5,	'img'),
(25,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">ECOSYSTEM PROTECTION ACTION PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To facilitate the initiative of protecting high conservation value areas and essential ecosystems from threats and damage. Outputs for this work may include the development of technical conservation work plans for each ecosystem, creation of forest mitigation security and forest  re patrols as  ell as wildlife protection actions.</p>',	'<strong style=\"text-align: center; font-weight: 800;\">ECOSYSTEM PROTECTION ACTION PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To facilitate the initiative of protecting high conservation value areas and essential ecosystems from threats and damage. Outputs for this work may include the development of technical conservation work plans for each ecosystem, creation of forest mitigation security and forest  re patrols as  ell as wildlife protection actions.</p>',	6,	'text'),
(26,	'our-program',	'hand-seed.png',	'hand-seed.png',	7,	'img'),
(27,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">ECOSYSTEM RESTORATION ACTION PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To facilitate the initiative of rehabilitating the habitat,  agship species and catchment areas that have a direct positive impact on the ecosystem’s ability to absorb and store carbon  Such plans may include the creation of a restoration task force, training modules, learning centers, and pilot projects for the restoration of endemic species habitats </p>',	'<strong style=\"text-align: center; font-weight: 800;\">ECOSYSTEM RESTORATION ACTION PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To facilitate the initiative of rehabilitating the habitat,  agship species and catchment areas that have a direct positive impact on the ecosystem’s ability to absorb and store carbon  Such plans may include the creation of a restoration task force, training modules, learning centers, and pilot projects for the restoration of endemic species habitats </p>',	8,	'img'),
(28,	'our-program',	'capacity.png',	'capacity.png',	9,	'img'),
(29,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">CAPACITY DEVELOPMENT PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To produce a prioritized agenda for partner’s capacity development that will execute the protection and restoration action programs in each grant distribution area in accordance with the ecosystem conservation plan (and other relevant plans    nd to implement tailored activities based on community needs to strengthen capacities at a system and individual level (including through interactive workshops).</p>',	'<strong style=\"text-align: center; font-weight: 800;\">CAPACITY DEVELOPMENT PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To produce a prioritized agenda for partner’s capacity development that will execute the protection and restoration action programs in each grant distribution area in accordance with the ecosystem conservation plan (and other relevant plans    nd to implement tailored activities based on community needs to strengthen capacities at a system and individual level (including through interactive workshops).</p>',	10,	'text'),
(30,	'our-program',	'community.png',	'community.png',	11,	'img'),
(31,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">COMMUNITY EMPOWERMENT PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To facilitate the initiative to increase community involvement in executing protection and restoration action programs; improve community access to sustainable forest management and forest resources; and increase the use of appropriate, environmentally friendly, lo  cost, technologies  ith sustainable end products that can be easily marketed </p>',	'<strong style=\"text-align: center; font-weight: 800;\">COMMUNITY EMPOWERMENT PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To facilitate the initiative to increase community involvement in executing protection and restoration action programs; improve community access to sustainable forest management and forest resources; and increase the use of appropriate, environmentally friendly, lo  cost, technologies  ith sustainable end products that can be easily marketed </p>',	12,	'text'),
(32,	'our-program',	'envi.png',	'envi.png',	13,	'img'),
(33,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">ENVIRONMENT AND NATURAL CONSERVATION EDUCATION.</strong>\r\n				<p class=\"p-t-30\">To facilitate the development of environmental researchs and conservation education programs that advance knowledge and learning about the grant distribution area, best practices and innovation among practitioners, and general conservation principles among school aged children.</p>',	'<strong style=\"text-align: center; font-weight: 800;\">ENVIRONMENT AND NATURAL CONSERVATION EDUCATION.</strong>\r\n				<p class=\"p-t-30\">To facilitate the development of environmental researchs and conservation education programs that advance knowledge and learning about the grant distribution area, best practices and innovation among practitioners, and general conservation principles among school aged children.</p>',	14,	'text'),
(34,	'our-program',	'technical.png',	'technical.png',	15,	'img'),
(35,	'our-program',	'<strong style=\"text-align: center; font-weight: 800;\">TECHNICAL ASSISTANCE AND MONITORING PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To support and oversee the execution of actions to protect high-value areas inside concession forest areas in natural or planted forests.</p>',	'<strong style=\"text-align: center; font-weight: 800;\">TECHNICAL ASSISTANCE AND MONITORING PROGRAM.</strong>\r\n				<p class=\"p-t-30\">To support and oversee the execution of actions to protect high-value areas inside concession forest areas in natural or planted forests.</p>',	16,	'text'),
(37,	'contact-us',	'<p><strong>BELANTARA FOUNDATION</strong><br />\r\nBoutique Office,3rd Floor, Jl. Timor No. 6, Menteng, RT.9/RW.4<br />\r\nGondangdia, Menteng, Jakarta<br />\r\nDaerah Khusus Ibukota Jakarta 10350<br />\r\nP:&nbsp;(021) 13915434</p>\r\n',	'<p><strong>BELANTARA FOUNDATION</strong><br />\r\nBoutique Office,3rd Floor, Jl. Timor No. 6, Menteng, RT.9/RW.4<br />\r\nGondangdia, Menteng, Jakarta<br />\r\nDaerah Khusus Ibukota Jakarta 10350<br />\r\nP:&nbsp;(021) 13915434</p>\r\n',	1,	'text');

